"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _codec = require("../codec");

var _IdentityFields = _interopRequireDefault(require("./IdentityFields"));

// Copyright 2017-2019 @polkadot/types authors & contributors
// This software may be modified and distributed under the terms
// of the Apache-2.0 license. See the LICENSE file for details.
describe('IdentityFields', () => {
  const registry = new _codec.TypeRegistry();
  it('has a custom u64 (64-bit) encodedLength', () => {
    expect(new _IdentityFields.default(registry).encodedLength).toEqual(8);
  });
  it('encodes a sample', () => {
    expect(new _IdentityFields.default(registry, ['Display', 'Legal']).valueEncoded.eqn(1 + 2)).toBe(true);
  });
  it('encodes to a valid hex value', () => {
    expect(new _IdentityFields.default(registry, ['Display', 'Legal']).toHex()).toEqual('0x0300000000000000');
  });
});